class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int x = 0, y = 0;
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                vector<int> c(2);
                c[grid[i][j]]++;
                c[grid[n-i-1][j]]++;
                c[grid[i][m-j-1]]++;
                c[grid[n-i-1][m-j-1]]++;
                ans += min(c[0], c[1]);
            }
        }
        int cnt = 0, ones = 0;
        if(n%2) {
            int row = n/2;
            for(int j=0;j<m/2;j++) {
                if(grid[row][j] != grid[row][m-j-1])
                    ans++, cnt++;
                else if(grid[row][j] == 1)
                    ones++;
            }
        }
        if(m%2) {
            int col = m/2;
            for(int i=0;i<n/2;i++) {
                if(grid[i][col] != grid[n-i-1][col])
                    ans++, cnt++;
                else if(grid[i][col] == 1)
                    ones++;
            }
        }
        if(m%2 && n%2 && grid[n/2][m/2] == 1)
            ans++; 
        if(ones%2 != 0) {
            if(cnt == 0)
                ans += 2;
        }
        return ans;
    }
};
