class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>> grid2=grid;
        int n=grid.size(),m=grid[0].size();

        int col=-1,row=-1;
        if(n%2)row=n/2;
        if(m%2)col=m/2;

        int cnt=0,ans=0;

        for(int i=0;i<n/2;i++){
            if(i==row)continue;
            for(int j=0;j<m/2;j++){
                int sum=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                if(grid[i][j]!=grid[i][m-1-j] || grid[i][j]!=grid[n-i-1][j] || grid[n-1-i][j]!=grid[n-1-i][m-1-j]){
                    ans+=min(4-sum,sum);
                    grid[i][j]=1;grid[i][m-1-j]=1;grid[n-1-i][j]=1;grid[n-1-i][m-1-j]=1;
                    grid2[i][j]=0;grid2[i][m-1-j]=0;grid2[n-1-i][j]=0;grid2[n-1-i][m-1-j]=0;
                }
            }
        }
        // cout<<ans<<endl;
        int cnt1=0,flip=0;
        for(int i=0;col!=-1 && i<n/2;i++){
            if(grid[i][col]!=grid[n-i-1][col]){
                ans++;
                flip++;
            }
            else if(grid[i][col]==1)cnt1++;
        }
        // cout<<cnt1<<' '<<flip<<endl;
        // cout<<ans<<endl;

        // cnt1=0,flip=0;
        for(int j=0;row!=-1 && j<m/2;j++){
            if(grid[row][j]!=grid[row][m-1-j]){
                ans++;
                flip++;
            }
            else if(grid[row][j]==1)cnt1++;
        }
        // cout<<cnt1<<' '<<flip<<endl;

        if(row!=-1 && col!=-1 && grid[n/2][m/2]==1)ans++;
        if(flip==0 && cnt1%2==1)ans+=2;
        return ans;
    }
};
