class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int N = g.size(), M = g[0].size();
        int res = 0;
        for (int i = 0; i < N / 2; i++) for (int j = 0; j < M / 2; j++) {
            int y = g[i][j] + g[i][M - 1 - j] + g[N - 1 - i][j] + g[N - 1 - i][M - 1 - j];
            res += min(y, 4 - y);
        }
        int cnt = 0, ones = 0;
        if (N % 2 == 1 && M % 2 == 1) {
            for (int j = 0; j < M / 2; j++) cnt += (g[N / 2][j] != g[N / 2][M - 1 - j]), ones += g[N / 2][j] + g[N / 2][M - 1 - j];
            for (int i = 0; i < N / 2; i++) cnt += (g[i][M / 2] != g[N - 1 - i][M / 2]), ones += g[i][M / 2] + g[N - 1 - i][M / 2];
            if (cnt == 0 && ones % 4 != 0) cnt = 2;
            cnt += g[N / 2][M / 2];
        }
        else if (N % 2 == 1) {
            for (int j = 0; j < M / 2; j++) cnt += (g[N / 2][j] != g[N / 2][M - 1 - j]), ones += g[N / 2][j] + g[N / 2][M - 1 - j];
            if (cnt == 0 && ones % 4 != 0) cnt = 2;
        }
        else if (M % 2 == 1) {
            for (int i = 0; i < N / 2; i++) cnt += (g[i][M / 2] != g[N - 1 - i][M / 2]), ones += g[i][M / 2] + g[N - 1 - i][M / 2];
            if (cnt == 0 && ones % 4 != 0) cnt = 2;
        }
        return res + cnt;
    }
};
