class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0,cnt01=0,cnt11=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt1=0;
                if(grid[i][j]) cnt1++;
                if(grid[n-i-1][j]) cnt1++;
                if(grid[i][m-j-1]) cnt1++;
                if(grid[n-i-1][m-1-j]) cnt1++;

                ans+=min(cnt1,4-cnt1);
            }
        }

        if(n&1){
            int i=n/2;
            for(int j=0;j<m/2;j++){
                if(grid[i][j]!=grid[i][m-j-1]) cnt01++;
                if(grid[i][j] && grid[i][m-j-1]) cnt11++;
            }
        }

        if(m&1){
            int j=m/2;
            for(int i=0;i<n/2;i++){
                if(grid[i][j]!=grid[n-i-1][j]) cnt01++;
                if(grid[i][j] && grid[n-i-1][j]) cnt11++;
            }
        }

        if(cnt11&1 && cnt01==0) ans+=2;
        ans+=cnt01;

        if(n&1 && m&1 && grid[n/2][m/2]) ans++;

        return ans;
    }
};
