class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size(), m = grid[0].size();
        int onePair = 0;
        int optionalPair = 0;

        for(int i = 0; i < (n + 1) / 2; i++) {
            for(int j = 0; j < (m + 1) / 2; j++) {
                int i1 = i, j1 = j, i2 = n - i1 - 1, j2 = m - j1 - 1;

                int ones = 0, zeros = 0;
                if(grid[i1][j1] == 1) ones++;
                else zeros++;
                if(grid[i1][j2] == 1) ones++;
                else zeros++;

                if(grid[i2][j1] == 1) ones++;
                else zeros++;
                if(grid[i2][j2] == 1) ones++;
                else zeros++;

                if(i1 == i2 && j1 == j2) {
                    if(grid[i1][j1] == 1) ans++;
                }
                else if(i1 == i2) {
                    if(grid[i1][j1] != grid[i1][j2]) {
                        ans++;
                        optionalPair++;
                    }
                    else if(grid[i1][j1] == 1) onePair++;
                }
                else if(j1 == j2) {
                    if(grid[i1][j1] != grid[i2][j1]) {
                        ans++;
                        optionalPair++;
                    }
                    else if(grid[i1][j1] == 1) onePair++;
                }
                else {
                    ans += min(ones, zeros);
                }

            } 
        }

        if(onePair % 2 == 0) {
            return ans;
        } else {
            if(optionalPair > 0) return ans;
            else return ans + 2;
        }
    }
};
