             ll ct1=0;
             if(v[i][j]) ct1++;
             if(v[i][m-j-1]) ct1++;
             if(v[n-i-1][j]) ct1++;
             if(v[n-i-1][m-j-1]) ct1++;
             ans+=min(ct1,4-ct1);
          }
        }
      //   cout<<(n%2 && !(m%2))<<endl;
        if(n%2 && !(m%2)){
           de("1")
           ll ct=0,val=0;
           for(int i=0;i<m/2;i++){
             if(v[n/2][i]==1 && v[n/2][m-i-1]==1) ct++;
             else if(v[n/2][i]!=v[n/2][m-i-1]) val++;
           }
           if((ct+val)%2==0){
             ans+=val;
           }
           else{
             if(val) ans+=val;
             else ans+=2;
           }
        }
        else if(m%2 && !(n%2)){
           de("2")
