             if(v[n/2][i]==1 && v[n/2][m-i-1]==1) ct++;
             else if(v[n/2][i]!=v[n/2][m-i-1]) val++;
           }
           for(int i=0;i<n/2;i++){
             if(v[i][m/2]==1 && v[n-i-1][m/2]==1) ct++;
             else if(v[i][m/2]!=v[n-i-1][m/2]) val++;
