class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])
        res = cnt = cnt2 = 0
        for i in range(n// 2):
            for j in range(m // 2):
                ones = 0
                for x, y in (i, j), (n - i - 1, j), (i, m - j - 1), (n - i - 1, m - j - 1):
                    ones += grid[x][y]
                res += min(ones, 4 - ones)
        if n & 1 and m & 1:
            res += grid[n // 2][m // 2]
        if n & 1:
            l, r = 0, m - 1
            while l < r:
                res += grid[n // 2][l] != grid[n // 2][r]
                if grid[n // 2][l] == grid[n // 2][r] == 1:
                    cnt += 1
                if grid[n // 2][l] != grid[n // 2][r]:
                    cnt2 += 1
                l += 1
                r -= 1
        if m & 1:
            l, r = 0, n - 1
            while l < r:
                res += grid[l][m // 2] != grid[r][m // 2]
                if grid[l][m // 2] == grid[r][m // 2] == 1:
                    cnt += 1
                if grid[l][m // 2] != grid[r][m // 2]:
                    cnt2 += 1
                l += 1
                r -= 1
        return res + 2 if cnt & 1 and cnt2 == 0 else res
