class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        inf = 10**10
        ans = [[inf] * 4 for _ in range(2)]
        p = 1
        ans[p][0] = 0

        for i in range((n + 1) // 2):
            for j in range((m + 1) // 2):
                p ^= 1
                ix = {(i, j), (i, m - j - 1), (n - i - 1, j), (n - i - 1, m - j - 1)}
                cz = sum(grid[i][j] == 1 for i, j in ix)
                co = len(ix) - cz
                for ones in range(4):
                    ans[p][ones] = min(inf, ans[1 - p][ones] + cz)
                for ones in range(4):
                    ans[p][ones] = min(ans[p][ones], ans[1 - p][(ones - len(ix)) % 4] + co)

        return ans[p][0]
