class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size(), ans = 0;
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int k = 0;
                k += grid[i][j];
                k += grid[m - 1 - i][j];
                k += grid[i][n - 1 - j];
                k += grid[m - 1 - i][n - 1 - j];
                if (k == 1 || k == 3) {
                    ans += 1;
                } else if (k == 2) {
                    ans += 2;
                }
            }
        }
        if (m % 2 == 1 && n % 2 == 1 && grid[m / 2][n / 2] == 1) {
            ans++;
        }
        int cnt = 0;
        int midRowDiff = 0, midColDiff = 0;
        if (m % 2 == 1) {
            for (int j = 0; j < n / 2; j++) {
                midRowDiff += grid[m / 2][j] != grid[m / 2][n - 1 - j];
                cnt += grid[m / 2][j] + grid[m / 2][n - 1 - j];
            }
        }
        if (n % 2 == 1) {
            for (int i = 0; i < m / 2; i++) {
                midColDiff += grid[i][n / 2] != grid[m - 1 - i][n / 2];
                cnt += grid[i][n / 2] + grid[m - 1 - i][n / 2];
            }
        }
        ans += midRowDiff + midColDiff;
        if (!midRowDiff && !midColDiff && cnt % 4 == 2) {
            ans += 2;
        }
        return ans;
    }
};
