class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int s=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int sum=0;
                sum+=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                s+=min(sum,4-sum);
            }
        }
        // cout<<"s="<<s<<endl;
        if(m%2==1&&n%2==0){
            int k=m/2,cnt01=0,cnt11=0;
            for(int i=0;i<n/2;i++){
                if(grid[k][i]^grid[k][n-1-i]){
                    cnt01++;
                }else if(grid[k][i]&grid[k][n-1-i]){
                    cnt11++;
                }
            }
            if(cnt11&1){
                if(!cnt01){
                    s+=2;
                }
            }
            s+=cnt01;
        }else if(n%2==1&&m%2==0){
            int k=n/2,cnt01=0,cnt11=0;
            for(int i=0;i<m/2;i++){
                if(grid[i][k]^grid[m-1-i][k]){
                    cnt01++;
                }else if(grid[i][k]&grid[m-1-i][k]){
                    cnt11++;
                }
            }
            if(cnt11&1){
                if(!cnt01){
                    s+=2;
                }
            }
            s+=cnt01;
        }else if(n%2==1&&m%2==1){
            int ki=m/2,kj=n/2,cnt01=0,cnt11=0;
            if(grid[ki][kj]){
                s++;
                grid[ki][kj]=0;
            }
            for(int i=0;i<n/2;i++){
                if(grid[ki][i]^grid[ki][n-1-i]){
                    cnt01++;
                }else if(grid[ki][i]&grid[ki][n-1-i]){
                    cnt11++;
                }
            }
            for(int i=0;i<m/2;i++){
                if(grid[i][kj]^grid[m-1-i][kj]){
                    cnt01++;
                }else if(grid[i][kj]&grid[m-1-i][kj]){
                    cnt11++;
                }
            }
            if(cnt11&1){
                if(!cnt01){
                    s+=2;
                }
            }
            s+=cnt01;
        }
        return s;
    }
};
