class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int tot=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                if(tot==0 || tot==4)continue;
                ans+=1;
                if(tot==2)ans+=1;
            }
        }
        if(n%2==1 and m%2==1)ans+=grid[n/2][m/2];
        if(n%2==0 and m%2==0) return ans;
        int zero=0,one=0,two=0;
        if(n%2==1){
            int i=0,j=m-1;
            while(i<j){
                int tot=grid[n/2][i]+grid[n/2][j];
                if(tot==0)zero+=1;
                else if(tot==1)one+=1;
                else two+=1;
                i++,j--;
            }
        }
        if(m%2==1){
            int i=0,j=n-1;
            while(i<j){
                int tot=grid[i][m/2]+grid[j][m/2];
                if(tot==0)zero+=1;
                else if(tot==1)one+=1;
                else two+=1;
                            i++,j--;
            }

        }
        ans+=one;
        if(two%2==1 and one==0)ans+=2;
        return ans;
    }
};
