class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size(),m=g[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int v=0;
                if(g[i][j]) v++;
                if(g[n-i-1][j]) v++;
                if(g[i][m-j-1]) v++;
                if(g[n-i-1][m-j-1]) v++;
                ans+=min(v,4-v);
            }
        }
        int cnt=0,p=0;
        // cout<<ans;
        if(n&1){
            for(int l=0,r=m-1;l<r;l++,r--){
                if(g[n/2][l]!=g[n/2][r]) cnt++;
                else if(g[n/2][l]) p++;
            }
        }
        if(m&1){
            for(int l=0,r=n-1;l<r;l++,r--){
                if(g[l][m/2]!=g[r][m/2]) cnt++;
                else if(g[l][m/2]) p++;
            }
        }
        if(n&1&&m&1){
            if(g[n/2][m/2]==1) ans++;
        }
        if(p%2==0) return ans+cnt;
        else{
            if(cnt) return ans+cnt;
            else return ans+cnt+2;
        }

    }
};
