class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<vector<array<int,2>>> v(n, vector<array<int,2>>(m, {0, 0}));
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                int rw = i, cl = j;
                if(i >= n / 2){
                    rw = n - 1 - i;
                } 
                if(j >= m / 2){
                    cl = m - 1 - j;
                }
                v[rw][cl][grid[i][j]] += 1;
            }
        }
        int cnt = 0;
        for(int i = 0; i < n / 2; ++i){
            for(int j = 0; j < m / 2; j++){
                cnt += min(v[i][j][0], v[i][j][1]);
            }
        }

        int f = 0;
        if(n & 1){
            for(int j = 0; j < m / 2; j++){
                if(grid[n / 2][j] != grid[n / 2][m - 1 - j]){
                    f = 1;
                    cnt += 1;
                }
            }
        }
        if(m & 1){
            for(int i = 0; i < n / 2; i++){
                if(grid[i][m / 2] != grid[n - 1 - i][m / 2]){
                    f = 1;
                    cnt += 1;
                }
            }
        }

        if((n & 1) && (m & 1)){
            cnt += grid[n / 2][m / 2];
        }

        if(!f){
            int temp = 0;
            if(n & 1){
                for(int j = 0; j < m / 2; j++){
                    if(grid[n / 2][j] == 1)
                        temp += 2;
                }
            }
            if(m & 1){
                for(int j = 0; j < n / 2; j++){
                    if(grid[j][m / 2] == 1)
                        temp += 2;
                }
            }
            if(temp % 4 != 0)
                cnt += 2;
        }
        return cnt;
    }
};
