class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n = len(grid), len(grid[0])
        res=0
        for i,j in product(range(m//2),range(n//2)):
            s=(grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j])
            res += min(s, 4-s)
        if m%2==1 and n%2==1 and grid[m//2][n//2]==1:
            grid[m//2][n//2]=0
            res+=1
        mr = grid[m//2]
        mc = [grid[i][n//2] for i in range(m)]
        def cal(l):
            return sum(l[i]!=l[len(l)-1-i] for i in range(len(l)//2))
        if m%2==1 and n%2==1:
            if grid[m//2][n//2]==1: res+=1
            t = cal(mr) + cal(mc)
            if t==0 and (sum(mr) + sum(mc))%4>0: t+=2
            return res + t
        elif m%2==1:
            t = cal(mr)
            if t==0 and sum(mr)%4>0: t+=2
            return res+t
        elif n%2==1:
            t=cal(mc)
            if t==0 and sum(mc)%4>0: t+=2
            return res+t
        return res
