class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        int ans = 0;
        for(int i = 0; i < n / 2; i++) {
            for(int j = 0; j < m / 2; j++) {
                int one = 0;
                one += a[i][j];
                one += a[i][m - j - 1];
                one += a[n - i - 1][j];
                one += a[n - i - 1][m - j - 1];
                ans += min(one, 4 - one);
            }
        }

        int cnt = 0;
        int one = 0;
        if(n & 1) {
            int mid = n / 2;
            int l = 0, h = m - 1;
            while(l < h) {
                if(a[mid][l] != a[mid][h]) {
                    ans++;
                    cnt++;
                } else {
                    if(a[mid][l] == 1) one += 2;
                }
                l++;
                h--;
            }
        }

        if(m & 1) {
            int mid = m / 2;
            int l = 0, h = n - 1;
            while(l < h) {
                if(a[l][mid] != a[h][mid]) {
                    ans++;
                    cnt++;
                } else {
                    if(a[l][mid] == 1) one += 2;
                }
                l++;
                h--;
            }
        }

        if(one % 4 == 2 && cnt == 0) ans += 2;

        if(n % 2 == 1 && m % 2 == 1) {
            if(a[n / 2][m / 2] == 1) ans++;
        }

        return ans;
    }
};
