#define MOD 1000000007
#define INF INT_MAX
#define endl "\n"
#define pb push_back
#define ppb pop_back
#define ff first
#define ss second
#define sz(x) ((int)(x).size())
#define all(x) (x).begin(), (x).end()

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = sz(grid);
        int m = sz(grid[0]);

        int ans = 0;
        int ones = 0;
        for(int i = 0; i < n / 2; i++) {
            for(int j = 0; j < m / 2; j++) {
                vector<int> cnt(2);

                cnt[grid[i][j]]++;
                cnt[grid[i][m - 1 - j]]++;
                cnt[grid[n - 1 - i][j]]++;
                cnt[grid[n - 1 - i][m - 1 - j]]++;

                ans += min(cnt[0], cnt[1]);
            }
        }

        if((n & 1) and (m & 1)) {
            if(grid[n / 2][m / 2]) 
                ans++;
        }

        int twice = 0;
        if(n & 1) {
            int i = n / 2;
            for(int j = 0; j < m / 2; j++) {
                vector<int> cnt(2);

                cnt[grid[i][j]]++;
                cnt[grid[i][m - 1 - j]]++;

                if(cnt[1] == 1) {
                    ans++;
                    twice++;
                }
                if(cnt[1] == 2) ones += 2;
            }
        }

        if(m & 1) {
            int j = m / 2;
            for(int i = 0; i < n / 2; i++) {
                vector<int> cnt(2);

                cnt[grid[i][j]]++;
                cnt[grid[n - 1 - i][j]]++;

                if(cnt[1] == 1) {
                    ans++;
                    twice++;
                }
                if(cnt[1] == 2) ones += 2;
            }
        }

        if(ones % 4) {
            if(!twice) ans += 2;  
        }

        return ans;
    }
};
