class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int cnt = 
                (grid[i][j] == 1) + 
                (grid[i][m - j - 1] == 1) + 
                (grid[n - i - 1][j] == 1) + 
                (grid[n - i - 1][m - j - 1] == 1);
                ans += min(cnt, 4 - cnt);
            }
        }

        if (n % 2 && m % 2) {
            if (grid[n / 2][m / 2] == 1) ans++;
        }

        int ans0 = 0;
        int cnt0 = 0;
        if (n % 2) {
            for (int j = 0; j < m / 2; j++) {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1]) ans0++;
                if (grid[n / 2][j] == 1 && grid[n / 2][m - j - 1] == 1) cnt0++;
            }
        }

        int ans1 = 0;
        int cnt1 = 0;
        if (m % 2) {
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) ans1++;
                if (grid[i][m / 2] == 1 && grid[n - i - 1][m / 2] == 1) cnt1++;
            }
        }

        if (ans0 == 0 && ans1 == 0 && (cnt0 + cnt1) % 2) ans += 2;
        return ans0 + ans1 + ans;
    }
};
