class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        vector<int> ct(5);
        for(int i=0;i<m/2;++i){
            for(int j=0;j<n/2;++j){
                int cur=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                ct[cur]++;
            }
        }
        int ans=0,ones=0,changed=0;
        ans+=ct[1]+ct[3]+2*ct[2];
        if(m%2==1){
            for(int i=0;i<n/2;++i){
                if(grid[m/2][i]!=grid[m/2][n-1-i]){
                    changed++;ans++;
                }
                else{
                    if(grid[m/2][i]==1) ones+=2;
                }
            }
        }
        if(n%2==1){
            for(int i=0;i<m/2;++i){
                if(grid[i][n/2]!=grid[m-1-i][n/2]){
                    changed++;ans++;
                }
                else{
                    if(grid[i][n/2]==1) ones+=2;
                }
            }
        }
        //cout<<ones<<" "<<changed<<endl;
        if(m%2==1 && n%2==1 && grid[m/2][n/2]==1) ans++;
        if(ones%4==2 && changed==0){
            ans+=2;
        }
        return ans;
    }
};
