impl Solution {
    pub fn min_flips(grid: Vec<Vec<i32>>) -> i32 {
        let m = grid.len();
        let n = grid[0].len();

        let mut count = 0;
        for i in 0..m / 2 {
            for j in 0..n / 2 {
                let x = grid[i][j] + grid[i][n - 1 - j]
                    + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j];
                count += x.min(4 - x);
            }
        }

        if m % 2 == 0 && n % 2 == 1 {
            let mut count2 = 0;
            let mut count3 = 0;
            for i in 0..m / 2 {
                count2 += grid[i][n / 2] + grid[m - 1 - i][n / 2];
                if grid[i][n / 2] != grid[m - 1 - i][n / 2] {
                    count3 += 1;
                }
            }
            if count3 == 0 && count2 % 4 == 2 {
                count += 2;
            } else {
                count += count3;
            }
        } else if m % 2 == 1 && n % 2 == 0 {
            let mut count2 = 0;
            let mut count3 = 0;
            for j in 0..n / 2 {
                count2 += grid[m / 2][j] + grid[m / 2][n - 1 - j];
                if grid[m / 2][j] != grid[m / 2][n - 1 - j] {
                    count3 += 1;
                }
            }
            if count3 == 0 && count2 % 4 == 2 {
                count += 2;
            } else {
                count += count3;
            }
        } else if m % 2 == 1 && n % 2 == 1 {
            let mut count2 = 0;
            let mut count3 = 0;
            for i in 0..m / 2 {
                count2 += grid[i][n / 2] + grid[m - 1 - i][n / 2];
                if grid[i][n / 2] != grid[m - 1 - i][n / 2] {
                    count3 += 1;
                }
            }
            for j in 0..n / 2 {
                count2 += grid[m / 2][j] + grid[m / 2][n - 1 - j];
                if grid[m / 2][j] != grid[m / 2][n - 1 - j] {
                    count3 += 1;
                }
            }
            if count3 == 0 && count2 % 4 == 2 {
                count += 2;
            } else {
                count += count3;
            }
            if grid[m / 2][n / 2] == 1 {
                count += 1;
            }
        }
        count
    }
}
