class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        ans=0
        mm=m//2
        mn=n//2
        cnt=0
        flx=0
        if m&1 and n&1:
            mm=(m-1)//2
            mn=(n-1)//2
            ans+=grid[mm][mn]

            for i in range(mm):
                if grid[i][mn]==grid[-i-1][mn]==1:
                    cnt+=2
                elif grid[i][mn]!=grid[-i-1][mn]:
                    ans+=1
                    flx+=1
            for j in range(mn):
                if grid[mm][j]==grid[mm][-j-1]==1:
                    cnt+=2
                elif grid[mm][j]!=grid[mm][-j-1]:
                    ans+=1
                    flx+=1
            #print(ans,cnt,flx)
            if cnt%4==2 and not flx:
                ans+=2
        elif m&1:
            mm=m//2
            mn=n//2
            for j in range(mn):
                if grid[mm][j]==grid[mm][-j-1]==1:
                    cnt+=2
                elif grid[mm][j]!=grid[mm][-j-1]:
                    ans+=1
                    flx+=1
            if cnt%4==2 and not flx:
                ans+=2
        elif n&1:

            for i in range(mm):
                if grid[i][mn]==grid[-i-1][mn]==1:
                    cnt+=2
                elif grid[i][mn]!=grid[-i-1][mn]:
                    ans+=1
                    flx+=1
            #print(ans,cnt,flx)
            if cnt%4==2 and not flx:
                ans+=2
       # print(ans)
        for i in range(mm):
            for j in range(mn):
                c=[grid[i][j],grid[-i-1][-j-1],grid[-i-1][j],grid[i][-j-1]]
                one=c.count(1)
                #print(i,j,one)
                ans+=min(one,4-one)
        return ans
