class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int u = 0, d = m - 1;
        int ans = 0;
        int[] tmp = new int[3];
        while (u <= d) {
            if (u == d) {
                int l = 0, r = n - 1;
                while (l <= r) {
                    if (l == r) {
                        int x = grid[u][l];
                        if (x == 1) ans++;
                    } else {
                        int x = grid[u][l], y = grid[d][r];
                        if (x == y) {
                            tmp[x]++;
                        } else {
                            tmp[2]++;
                        }
                    }
                    l++;
                    r--;
                }


            } else {
                int l = 0, r = n - 1;
                while (l <= r) {
                    if (l == r) {
                        int x = grid[u][l], y = grid[d][l];
                        if (x == y) {
                            tmp[x]++;
                        } else {
                            tmp[2]++;
                        }
                    } else {
                        int[] cnt = new int[2];
                        cnt[grid[u][l]]++;
                        cnt[grid[u][r]]++;
                        cnt[grid[d][l]]++;
                        cnt[grid[d][r]]++;
                        ans += Math.min(cnt[0], cnt[1]);
                    }
                    l++;
                    r--;
                }
            }
            u++;
            d--;
        }
        if (tmp[1] % 2 == 0) {
            return ans + tmp[2];
        } else {
            if (tmp[2] > 0) {
                return ans + tmp[2];
            } else {
                return ans + 2;
            }
        }


    }
}
