class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = INT_MAX, tmp = 0;
        int sp = 0, s0 = 0, s1 = 0;
        int sum = 0;
        for (int i=0; i<n; i++)
            for (int j=0; j<m; j++) sum+=a[i][j];
        vector b(n, vector(m, 0));
        vector<pair<int, int>> v;
        for (int i=0; i<n; i++){
            for (int j=0; j<m; j++){
                if (b[i][j]) continue;
                vector<int> sb(2);
                sb[a[i][j]]++;
                b[i][j]++;
                if (m-j-1!=j) sb[a[i][m-j-1]]++, b[i][m-j-1]++;
                if (i!=n-i-1) sb[a[n-i-1][j]]++, b[n-i-1][j]++;
                if (i!=n-i-1 && j!=m-j-1) sb[a[n-i-1][m-j-1]]++, b[n-i-1][m-j-1]++;
                // if (sb[0] + sb[1] == sb[0] || sb[0] + sb[1] == sb[1]) continue;
                v.push_back({sb[0]+sb[1], sb[1]});
            }
        }
        // cout<<v.size()<<endl;
        vector dp(v.size()+1, vector(4, INT_MAX/2));
        dp[0][sum%4] = 0;
        for (int i=0; i<v.size(); i++){
            auto [x, y] = v[i];
            // cout<<x<<' '<<y<<endl;
            for (int j=0; j<4; j++){

                dp[i+1][(j+(x-y))%4] = min(dp[i+1][(j+(x-y))%4], dp[i][j] + x-y);
                // cout<<1;
                dp[i+1][(j-y+4)%4] = min(dp[i+1][(j-y+4)%4], dp[i][j] + y);
            }
        }
        return dp[v.size()][0];
    }
};
