class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size();
        int m=a[0].size();

        int c=0;
        int cli=0;
        int gli=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int pli=n-1-i;
                int plj=m-1-j;
                int val = a[i][j]+a[pli][j]+a[i][plj]+a[pli][plj];
                c+=min(val,4-val);
            }
        }

        if(m%2==1){
            int mm=m/2;
            for(int i=0;i<n/2;i++){
                 int pli=n-1-i;
                int val=a[i][mm]+a[pli][mm];
                if(val==2)cli++;
                  else if(val==1){gli++;
                                c+=val;
                               }
                else c+=val;
            }
        }
         if(n%2==1){
            int nn=n/2;
            for(int i=0;i<m/2;i++){
                 int plj=m-1-i;
                int val=a[nn][i]+a[nn][plj];
                if(val==2)cli++;
                else if(val==1){gli++;
                                c+=val;
                               }
                else c+=val;
            }
        }

        if(m%2==1 && n%2==1){
            int mm=m/2 ; 
             int nn=n/2 ;
            c+=a[nn][mm];
        }

        cli*=2;
        if(gli==0)
        c+= (cli%4);

        return c;
    }
};
