class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(); 
        vector<vector<bool>> st(n + 1, vector<bool>(m + 1, 0)); 
        #define x first 
        #define y second 
        using PII = pair<int,int>; 
        int ans = 0, cnt = 0; 
        vector<PII>v;
        for(int i = 0; i < n; ++ i ) {
            for(int j = 0; j < m; ++ j ) {
                if(st[i][j]) continue; 
                // st[i][j] = 1; 
                PII p1 = {i, j}, p2 = {n - 1 - i, j}, 
                p3 = {i, m - 1 - j}, p4 = {n - 1 - i, m - 1 - j}; 
                set<PII>se;
                if(!st[p1.x][p1.y])se.insert(p1),st[p1.x][p1.y]=1;
                if(!st[p2.x][p2.y])se.insert(p2), st[p2.x][p2.y]=1;
                if(!st[p3.x][p3.y])se.insert(p3), st[p3.x][p3.y]=1;
                if(!st[p4.x][p4.y])se.insert(p4), st[p4.x][p4.y]=1;
                int c1 = 0, c2 = 0; 
                for(auto t : se) {
                    if(grid[t.x][t.y] == 1) c1++;
                    else c2++;
                }
                v.push_back({c1, c2}); 
            }
        }
        int dp[200010][4]; 
        for(int i = 0; i <= v.size(); ++ i ) 
            for(int j = 0; j < 4; ++ j ) dp[i][j] = 1e9; 
        PII t = v[0]; 
        dp[1][(t.x + t.y) % 4] = t.y;  
        dp[1][0]=min(dp[1][0],t.x);

        // cout<<v.size()<<endl;
        for(int i = 2; i <= v.size(); ++ i ) {
            t = v[i - 1]; 
            for(int j = 0; j <= 3; ++ j ) {
                // 当前取1
                if(dp[i - 1][j] == 1e9) continue;
                dp[i][(j+t.x+t.y)%4]=min(dp[i][(j+t.x+t.y)%4],dp[i-1][j]+t.y); 
                // 不取1
                dp[i][j] = min(dp[i][j], dp[i - 1][j]+t.x);
            }
        }
        return dp[v.size()][0];  
    }

};
