class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size();
        int m=a[0].size();


        int i=0,ii=n-1;
        int ans=0;
        int two=0;
        int one=0;

        while(i<ii){

            int j=0,jj=m-1;
            while(j<jj) {
                int count[2]={};
                count[a[i][j]]++;
                count[a[i][jj]]++;
                count[a[ii][j]]++;
                count[a[ii][jj]]++;

                if(count[0]!=4 && count[1]!=4){
                    ans+=(4-max(count[0],count[1]));
                }
                j++,jj--;
            }

            if(j==jj) {
                if(a[i][j]!=a[ii][j]) {
                    ans+=1;
                    one++;
                }else {
                    if(a[i][j]==1) {
                        two++;
                    }
                }
            }

            i++,ii--;
        }

        if(i==ii) {
            int j=0,jj=m-1;
            while(j<jj) {
                if(a[i][j]!=a[i][jj]) {
                    ans+=1;
                    one++;
                }else {
                    if(a[i][j]==1) {
                        two++;
                    }
                }
                j++,jj--;
            }
            if(j==jj) {
                if(a[i][j]) {
                    ans++;
                }
            }
        }

        if(two%2) {
            if(one==0) {
                ans+=2;
            }
        }
        return ans;
    }
};
