class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        int cnt=0;
        for(int i=0;i<n/2;i++)
            for(int j=0;j<m/2;j++){
                map<int,int>mp;
                mp[grid[i][j]]++;
                mp[grid[n-i-1][j]]++;
                mp[grid[i][m-j-1]]++;
                mp[grid[n-i-1][m-j-1]]++;
                ans+=min(mp[1],mp[0]);
        }
        int v=0;
        if(n%2==1){
            int l=0,r=m-1;
            int i=n/2;
            while(l<r){
                if(grid[i][l]!=grid[i][r])ans++,v++;
                else if(grid[i][l]==1)cnt+=2;
                l++,r--;
            }
        }
        if(m%2==1){
            int l=0,r=n-1;
            int j=m/2;
            while(l<r){
                if(grid[l][j]!=grid[r][j])v++,ans++;
                else if(grid[l][j]==1)cnt+=2;
                l++,r--;
            }
        }
        if(n%2==1&&m%2==1&&grid[n/2][m/2]==1)ans++;
        if(cnt%4&&v==0)ans+=2;
        return ans;
    }
};
