class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int num1=0;
                num1+=grid[i][j];
                num1+=grid[i][n-1-j];
                num1+=grid[m-1-i][j];
                num1+=grid[m-1-i][n-1-j];
                ans+=min(num1,4-num1);
            }
        }
        //printf("%d\n",ans);
        int num1=0;
        int no=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]==grid[m-1-i][n/2]&&grid[i][n/2]==1)num1+=2;
                else if(grid[i][n/2]!=grid[m-1-i][n/2])no++;
            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]==grid[m/2][n-1-i]&&grid[m/2][i]==1)num1+=2;
                else if(grid[m/2][i]!=grid[m/2][n-1-i])no++;
            }
        }
        //printf("%d %d\n",num1,no);
        if(no==0&&num1%4==2){
            ans+=2;
        }
        else{
            ans+=no;
        }
        if(n%2&&m%2&&grid[m/2][n/2])ans++;
        return ans;
    }
};
