class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        int c1 = 0, c2 = m - 1;
        int ans = 0;
        int ones = 0;

        int tog = 0;
        int cntOnes;
        while(c1 <= c2)
        {
            int r1 = 0, r2 = n - 1;
            while(r1<=r2)
            {
                if(r1 == r2 && c1==c2)
                {
                    if(a[r1][c1])
                        ans++;
                    r1++,r2--;
                    continue;
                }
                if(r1==r2 || c1 == c2)
                {
                    if(a[r1][c1]!=a[r2][c2])
                    {
                        tog = 1;
                        ans++;
                    }
                    else
                    {
                        if(a[r1][c1])
                        {
                            cntOnes+=2;
                        }
                    }
                    r1++,r2--;
                    continue;
                }


                map<int,int> mp;
                mp[a[r1][c1]]++;
                mp[a[r1][c2]]++;
                mp[a[r2][c1]]++;
                mp[a[r2][c2]]++;

                ans += min(mp[1], mp[0]);
                r1++,r2--;
            }
            c1++,c2--;
        }

        if(cntOnes%4)
        {
            if(tog==0)
            ans+=2;
        }
        return ans;
    }
};
