class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size();
        int m=g[0].size();
        int ans=0;




        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int count=g[i][j]+g[i][m-1-j]+g[n-1-i][j]+g[n-1-i][m-1-j];
                ans+=min(count,4-count);

            }
        }



        int s=0,t=0,count1=0, count2=0;

        if(n%2==1){  
            int i=0, j=m-1;
            while(i<j){
                if(g[n/2][i]!=g[n/2][j]){
                    count1++;
                    s++;
                }
                if(g[n/2][i]==1 && g[n/2][m-1-i]==1) t++;

                i++;
                j--;
            }

        }


        if(m%2==1){
            int i=0, j=n-1;
            while(i<j){
                if(g[i][m/2]!=g[j][m/2]){
                    count2++;
                    s++;
                }
                if(g[i][m/2]==1 && g[j][m/2]==1) t++;

                i++;
                j--;
            }
        }

        if(n%2==1 && m%2==1) ans+=g[n/2][m/2];


        if(s>0 || t%2==0) ans+=count1+count2;
        else ans+=count1+count2+2;


        return ans;



    }
};
