class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=0;
                if(grid[i][j]==0) cnt++;
                if(grid[i][m-j-1]==0) cnt++;
                if(grid[n-i-1][j]==0) cnt++;
                if(grid[n-i-1][m-j-1]==0) cnt++;
                ans+=min(cnt,4-cnt);
            }
            // cout<<cnt<<"\n";

        }

        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1) ans++;
            int cnt=0;
            int ones=0;
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1]) cnt++;
                else if(grid[n/2][j]==1) ones+=2;
            }
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2]) cnt++;
                else if(grid[i][m/2]==1) ones+=2;
            }
            if(ones%4==0) ans+=cnt;
            else if(cnt>0) ans+=cnt;
            else ans+=2;
        }
        else if(n%2==1){
            int cnt=0;
            int ones=0;
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1]) cnt++;
                else if(grid[n/2][j]==1) ones+=2;
            }
            if(ones%4==0) ans+=cnt;
            else if(cnt>0) ans+=cnt;
            else ans+=2;
        }
        else if(m%2==1){
            int cnt=0;
            int ones=0;
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2]) cnt++;
                else if(grid[i][m/2]==1) ones+=2;
            }
            if(ones%4==0) ans+=cnt;
            else if(cnt>0) ans+=cnt;
            else ans+=2;
        }
        return ans;

    }
};
