class Solution {
    public int minFlips(int[][] grid) {
        int x = grid.length;
        int y = grid[0].length;
        int flips = 0;
        for (int i = 0; i < x/2; ++i) {
            for (int j = 0; j < y/2; ++j) {
                int count = 0;
                count += grid[i][j] == 1 ? 1 : 0;
                count += grid[i][y-j-1] == 1 ? 1 : 0;
                count += grid[x-i-1][j] == 1 ? 1 : 0;
                count += grid[x-i-1][y-j-1] == 1 ? 1 : 0;
                flips += Math.min(count, 4-count);
            }
        }
        int ones = 0;
        int mflips = 0;
        if (x%2 == 1) {
            for (int j = 0; j < y/2; ++j) {
                if (grid[x/2][j] != grid[x/2][y-j-1]) {
                    ++mflips;
                }
                if (grid[x/2][j] == 1) {
                    ++ones;
                }
            }
        }
        if (y%2 == 1) {
            for (int i = 0; i < x/2; ++i) {
                if (grid[i][y/2] != grid[x-i-1][y/2]) {
                    ++mflips;
                }
                if (grid[i][y/2] == 1) {
                    ++ones;
                }
            }
        }
        flips += mflips;
        if (mflips == 0 && ones%2 == 1) {
            flips += 2;
        }
        if (x%2 == 1 && y%2 == 1 && grid[x/2][y/2] == 1) {
            ++flips;
        }
        return flips;
    }
}
