class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                a, b, c, d = grid[i][j], grid[i][n - j - 1], grid[m - i - 1][n - j - 1], grid[m - i - 1][j]
                tot = sum([a,b,c,d])
                ans += min(tot, 4 - tot)
        flag = False
        def comp (arr):
            ln = len(arr)
            nonlocal ans, flag
            res = 0
            tot = 0
            for i in range(ln // 2):
                if arr[i] != arr[ln - i - 1]:
                    flag = True
                    ans += 1
                elif arr[i] == 1:
                    res += 1
            return res

        f1 = f2 = 0
        if m % 2:
            f1 = comp([grid[m // 2][i] for i in range(n)])
        if n % 2:
            f2 = comp([grid[i][n // 2] for i in range(m)])
        if m % 2 and n % 2 and grid[m // 2][n // 2] == 1:
            ans += 1
        if not flag and (f1 + f2) % 2:
            ans += 2
        return ans
