class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        for (int i = 0; i < n/2; i++) {
            for (int j = 0; j < m/2; j++) {
                int top = i, bottom = n-1-i;
                int left = j, right = m-1-j;
                int cnt0 = 0, cnt1 = 0;

                if (grid[top][left]==1) cnt1++;
                else cnt0++;

                if (grid[top][right]==1) cnt1++;
                else cnt0++;

                if (grid[bottom][left]==1) cnt1++;
                else cnt0++;

                if (grid[bottom][right]==1) cnt1++;
                else cnt0++;

                ans += min(cnt0,cnt1);
            }
        }

        int cnt0 = 0, cnt1 = 0, diff = 0;
        if (n%2==1) {
            int l = 0, r = m-1, mid = n/2;
            while (l<r) {
                if (grid[mid][l]!=grid[mid][r]) diff++;
                else if (grid[mid][l]==1) cnt1++;
                else cnt0++;
                l++, r--;
            }
        }

        if (m%2==1) {
            int l = 0, r = n-1, mid = m/2;
            while (l<r) {
                if (grid[l][mid]!=grid[r][mid]) diff++;
                else if (grid[l][mid]==1) cnt1++;
                else cnt0++;
                l++, r--;
            }
        }

        if (cnt1%2==1 && diff==0) ans += 2;
        ans += diff;

        if (n%2 && m%2) 
            ans += (grid[n/2][m/2]==1);

        return ans;
    }
};
