class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int x = 0;
        for (int i = 0; i < n / 2; i++)
            for (int j = 0; j < m / 2; j++) {
                vector<int> cnt(2);
                cnt[grid[i][j]]++;
                cnt[grid[n-i-1][j]]++;
                cnt[grid[i][m-j-1]]++;
                cnt[grid[n-i-1][m-j-1]]++;
                x += min(cnt[0], cnt[1]);
            }
        int y = 0;
        if (n & 1) {
            if (m & 1) {
                if (grid[n/2][m/2]) x++;
                int one = 0;
                for (int i = 0; i < n / 2; i++) {
                    if (grid[i][m/2] != grid[n-i-1][m/2]) y++;
                    else if (grid[i][m/2] == 1) one += 2;
                }
                for (int j = 0; j < m / 2; j++) {
                    if (grid[n/2][j] != grid[n/2][m-j-1]) y++;
                    else if (grid[n/2][j] == 1) one += 2;
                }
                if (one % 4 == 2 && y == 0) y += 2;
            } else {
                int one = 0;
                for (int j = 0; j < m / 2; j++) {
                    if (grid[n/2][j] != grid[n/2][m-j-1]) y++;
                    else if (grid[n/2][j] == 1) one += 2;
                }
                if (one % 4 == 2 && y == 0) y += 2;
            }
        } else {
            if (m & 1) {
                int one = 0;
                for (int i = 0; i < n / 2; i++) {
                    if (grid[i][m/2] != grid[n-i-1][m/2]) y++;
                    else if (grid[i][m/2] == 1) one += 2;
                }
                if (one % 4 == 2 && y == 0) y += 2;
            }
        }
        return x + y;
    }
};
