class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = sz(a);
        int m = sz(a[0]);

        int ans = 0;
        rep(i, 0, n/2) rep(j, 0, m/2) {
            int cnt = a[i][j] + a[n - i - 1][j] + a[i][m - j - 1] + a[n - i - 1][m - j - 1];
            ans += min(cnt, 4 - cnt);
        }

        vector cnt(2, vector<int>(2));

        if (n % 2 == 1) {
            rep(j, 0, m/2) {
                cnt[a[n/2][j]][a[n/2][m - j - 1]]++;
            }
        }
        if (m % 2 == 1) {
            rep(i, 0, n/2) {
                cnt[a[i][m/2]][a[n - i - 1][m/2]]++;
            }
        }

        ans += cnt[0][1] + cnt[1][0];

        if (cnt[1][1] % 2 == 1 && max(cnt[0][1], cnt[1][0]) == 0) {
            ans += 2;
        }

        if (n % 2 == 1 && m % 2 == 1) ans += a[n/2][m/2];
        return ans;
    }
};


