class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        pair<int,int> p1,p2,p3,p4;
        p1 = {0,0},p2 = {0,m-1},p3 = {n-1,0},p4 = {n-1,m-1};
        int ans = 0;
        while(1){

            if(p1.first >= p3.first) break;
            pair<int,int> t1,t2,t3,t4;
            t1 = p1,t2 = p2,t3 = p3,t4 = p4;
            while(1){
                if(t1.second >= t2.second){
                    break;
                }
                int ct = 0;
                if(grid[t1.first][t1.second] == 1){
                    ct++;
                }
                if(grid[t2.first][t2.second] == 1){
                    ct++;
                }
                if(grid[t3.first][t3.second] == 1){
                    ct++;
                }
                if(grid[t4.first][t4.second] == 1){
                    ct++;
                }
                int rem = 4 - ct;
                ans += min(ct,rem);
                t1.second++;
                t2.second--;
                t3.second++;
                t4.second--;

            }
            p1.first++;
            p2.first++;
            p3.first--;
            p4.first--;
        }
        if(n % 2 == 1 && m % 2 == 1){
            if(grid[n/2][m/2] == 1) ans++;
            grid[n/2][m/2] = 0;
        }
        int oneonepair = 0,onezeropair = 0,zerozeropair = 0;
        if(n % 2 == 1){
            int ptr1 = 0,ptr2 = m-1;
            while(ptr1 < ptr2){
                if(grid[n/2][ptr1] == 1 && grid[n/2][ptr2] == 1){
                    oneonepair++;
                }
                else if(grid[n/2][ptr1] == 0 && grid[n/2][ptr2] == 0){
                    zerozeropair++;
                }
                else{
                    onezeropair++;
                }
                ptr1++;
                ptr2--;
            }
        }
        if(m % 2 == 1){

            int ptr1 = 0,ptr2 = n-1;
            while(ptr1 < ptr2){
                if(grid[ptr1][m/2] == 1 && grid[ptr2][m/2] == 1){
                    oneonepair++;
                }
                else if(grid[ptr1][m/2] == 0 && grid[ptr2][m/2] == 0){
                    zerozeropair++;
                }
                else{
                    onezeropair++;
                }
                ptr1++;
                ptr2--;
            }

        }
        if((oneonepair * 2) % 4 == 0){
            ans += onezeropair;
        }
        else{
            if(onezeropair > 0){
                ans += onezeropair;
            }
            else{
                ans += 2;
            }
        }
        // cout<<oneonepair<<" "<<onezeropair<<" "<<zerozeropair<<endl;
        return ans;

    }
};
