class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(); 
        int m = g[0].size(); 
        int temp=0;
        for(int i=0;i<n;i++) temp++;
         for(int i=0;i<m;i++) temp++;
        int  response = 0;
        int temp1=n/2;
        int temp2=m/2;
        if ((n % 2)==1 && (m % 2)==1 && g[temp1][temp2]) {response = response+ 1;}
        vector<int> count1(3, 0);
        if (n % 2==1) {
            int x1 = 0, y1 = m - 1;
            while (x1 < y1) {
                int c = g[n / 2][x1] + g[n / 2][y1];
                count1[c] += 1;
                x1 += 1;
                y1 -= 1;
            }
        }
        if (m % 2) {
            int x1 = 0, y1 = n - 1;
            while (x1 < y1) {
                int c = g[x1][m / 2] + g[y1][m / 2];
                count1[c] += 1;
                x1 += 1;
                y1 -= 1;
            }
        }
        // if (count[2] % 2) response += (count[1] > 0) ? count[1] : 2;
        if(count1[2]%2) 
        {
            if(count1[1]>0 ) response+=count1[1];
            else response+=2;
        }
        else response += count1[1];
        for (int i = 0; i < temp1; ++i) {
            for (int j = 0; j < temp2; ++j) {
                int c11 = g[i][j], c21 = g[n - 1 - i][j];
                int c31 = g[i][m - 1 - j], c41 = g[n - 1 - i][m - 1 - j];
                int at11 = c11 + c21 + c31 + c41;
                if (at11 && at11 != 4) response+= min(at11, 4 - at11);
            }
        }
        return response;
    }
};
