class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        int cone=0;
        int flag=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int z=0,one=0;
                if(grid[i][j])one++;
                else z++;
                if(grid[n-1-i][j])one++;
                else z++;
                if(grid[i][m-1-j])one++;
                else z++;
                if(grid[n-1-i][m-1-j])one++;
                else z++;
                ans+=min(one,z);
            }
        }
        if(n%2){
            int i=n/2;
            for(int j=0;j<m/2;j++){
                int z=0,one=0;
                if(grid[i][j])one++;
                else z++;
                if(grid[i][m-1-j])one++;
                else z++;
                ans+=min(one,z);
                if(one>z)cone+=one+z;
                if(one==1&&z==1)flag=1;
               }
        }
        if(m%2){
            int j=m/2;
            for(int i=0;i<n/2;i++){
                int z=0,one=0;
                if(grid[i][j])one++;
                else z++;
                if(grid[n-1-i][j])one++;
                else z++;
                ans+=min(one,z);
                if(one>z)cone+=one+z;
                if(one==1&&z==1)flag=1;
                }
        }    
        if(n%2&&m%2)if(grid[n/2][m/2])ans++;
        if(cone%4==2&&flag!=1)ans+=2;
        return ans;
    }
};
