class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int x=0,y=0;
                if(grid[i][j]==0)x++;
                else y++;
                if(grid[n-i-1][j]==0)x++;
                else y++;
                if(grid[i][m-j-1]==0)x++;
                else y++;
                if(grid[n-i-1][m-j-1]==0)x++;
                else y++;
                ans+=min(x,y);
            }
        }
        if(m%2==1&&n%2==1){
            if(grid[n/2][m/2]==1)ans++;
        }
        int l=0,r=0,p=0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2])l++;
                else if(grid[i][m/2]==1)p+=2;
            }
        }
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-i-1])r++;
                else if(grid[n/2][i]==1)p+=2;
            }
        }
        if(l==0&&r==0){
            if(p%4)return ans+=2;
            else return ans;
        }
        return ans+l+r;
    }
};
