class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i, j, k, n = grid.size(), m = grid[0].size();
        int equal = 0;
        int row = 0, col = 0;
        int ans = 0;
        int count = 0;
        for (i = 0; i < n / 2 + n % 2; i++) {
            for (j = 0; j < m / 2 + m % 2; j++) {
                int one = 0;
                int zero = 0;
                if (grid[i][j] == 0)
                    zero++;
                else
                    one++;
                if (grid[n - i - 1][m - j - 1] == 0)
                    zero++;
                else
                    one++;
                if (grid[i][m - j - 1] == 0)
                    zero++;
                else
                    one++;
                if (grid[n - i - 1][j] == 0)
                    zero++;
                else
                    one++;
                if (i == n / 2) {
                    one /= 2;
                    zero /= 2;
                    if (min(one, zero) > 0)
                        row++;
                }
                if (j == m / 2) {
                    one /= 2;
                    zero /= 2;
                    if (min(one, zero) > 0)
                        col++;
                }
                if (i == n / 2 && j == m / 2) {
                    if(grid[i][j] == 1)
                        equal++;
                }
                ans += min(one, zero);
                if (one > zero)
                    count += (one + zero);
                // if(zero >= one) {
                //     grid[i][j] = 0;
                //     grid[n-i-1][m-j-1] = 0;
                //     grid[i][m-j-1] = 0;
                //     grid[n-i-1][j] = 0;
                // }
                // else {
                //     grid[i][j] = 1;
                //     grid[n-i-1][m-j-1] = 1;
                //     grid[i][m-j-1] = 1;
                //     grid[n-i-1][j] = 1;
                // }
                // if(one == zero)
                //     equal++;
                // cout<<min(one,zero)<<" ";
            }
            // cout<<endl;
        }
        count %= 4;
        if(max(row,col) > 0 && count >= 2) 
            count -= 2;
        // if(equal == 1 && count%2 == 1) {
        //     // count--;
        //     ans++;
        // }
        // cout << ans << " " << count << " " << row << " " << col << endl;
        ans += count;
        return ans;
    }
};
