class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(),m = g[0].size();
        int l = 0,r = n - 1;
        int res = 0;
        while(l < r){
            int i = 0,j = m - 1;
            while(i < j){
                int a = g[l][i] + g[l][j] + g[r][i] + g[r][j];
                res += min(a,4 - a);
                i++;j--;
            }
            l++;r--;
        }
        // cout<<res<<endl;
        int a = 0,b = 0,c = 0;
        if((m % 2) && (n % 2)){res += g[n/2][m/2];}
        if(m % 2){
            int p = m/2;
            l = 0;r = n - 1;
            while(l < r){
                if(g[l][m/2] == g[r][m/2] && g[l][m/2])a++;
                else if(g[l][m/2] == g[r][m/2] && g[l][m/2] == 0)b++;
                else c++;
                l++;r--;
            }
        }
        if(n % 2){
            int p = n/2;
            l = 0;r = m - 1;
            while(l < r){
                if(g[n/2][l] == g[n/2][r] && g[n/2][l])a++;
                else if(g[n/2][l] == g[n/2][r] && g[n/2][l] == 0)b++;
                else c++;
                l++;r--;
            }
        }
        if((2*a)%4){
            if(c == 0)res += 2;
        }
        res += c;
        return res;
    }
};
