class Solution:
    def minFlips(self, arr: List[List[int]]) -> int:
            n=len(arr)
            m=len(arr[0])
            ans=0
            for i in range(n//2):
                for j in range(m//2):
                    cur=arr[i][j]+arr[n-1-i][j]+arr[i][m-1-j]+arr[n-1-i][m-1-j]
                    cur=min(cur,4-cur)
                    ans+=cur
            onen=0
            an=0
            if n%2:
                for j in range(m//2):
                    cur=arr[n//2][j]+arr[n//2][m-1-j]
                    if cur==1:
                        an+=1
                    elif cur==2:
                        onen+=2
                    ans+=min(cur,2-cur)
            if m%2:
                for i in range(n//2):
                    cur=arr[i][m//2]+arr[n-1-i][m//2]
                    if cur==1:
                        an+=1
                    elif cur==2:
                        onen+=2
                    ans+=min(cur,2-cur)
            if n%2 and m%2:
                if arr[n//2][m//2]==1:
                    ans+=1
            if onen%4==0:
                return ans
            if onen%4==2:
                if an==0:
                    return ans+2
                return ans
