class Solution {
public:
  int minFlips(vector<vector<int>>& g) {
    int r = 0;
    const int n = g.size(), m = g[0].size();
    for (int i = 0, l = n - 1; i < l; ++i, --l)
        for (int j = 0, k = m - 1; j < k; ++j, --k) {
            int t = g[i][j] + g[i][k] + g[l][j] + g[l][k];
            r += min(t, 4 - t);
        }
    const int u = n >> 1, v = m >> 1;
    if ((n & 1) && (m & 1)) r += g[u][v];
    int s = 0, o = 0;
    if (m & 1) for (int i = 0, l = n - 1; i < l; ++i, --l) { s += g[i][v] ^ g[l][v]; o += g[i][v] + g[l][v]; }
    if (n & 1) for (int j = 0, k = m - 1; j < k; ++j, --k) { s += g[u][j] ^ g[u][k]; o += g[u][j] + g[u][k]; }
    return r + q(s, o);
  }

  int q(int x, int o) {
    for ( ;; x += 2)
    for (int t = x; t >= 0 && t >= x - 8; t -= 2)
        if ((o + t) % 4 == 0 || (o >= t && (o - t) % 4 == 0)) return x;
  }
};
