class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int r = grid.size(), c = grid[0].size();
        int cnt = 0, odd = 0, oddd = 0, bad = 0;
        for(int i=0; i<r; i++)
            for(int j=0; j<c; j++){
                int v = grid[i][j] + grid[i][c-j-1] + grid[r-i-1][j] + grid[r-i-1][c-j-1];
                if(i == r-i-1 && j == c-j-1){
                    bad = grid[i][j];
                } else if(i == r-i-1 || j == c-j-1){
                    if(v == 2){
                        odd += 1;
                    } else if (v == 4){
                        oddd += 1;
                    }
                } else {
                    cnt += min(4-v, v);
                }
            }
        cout << cnt << " " << odd << " " << oddd << " " << bad << endl;
        return cnt / 4 + odd/2 + ((oddd / 2) % 2 && !odd) * 2 + bad;
    }
};
