class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        if (n * m < 4) {
            int cnt = 0;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    cnt += grid[i][j];
                }
            }
            return cnt;
        }
        int res = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int x1 = i, y1 = j;
                int x2 = i, y2 = m - j - 1;
                int x3 = n - i - 1, y3 = j;
                int x4 = n - i - 1, y4 = m - j - 1;
                vector<int> cnt(2);
                cnt[grid[x1][y1]]++;
                cnt[grid[x2][y2]]++;
                cnt[grid[x3][y3]]++;
                cnt[grid[x4][y4]]++;
                res += min(cnt[0], cnt[1]);
            }
        }
        int cnt01 = 0, cnt00 = 0, cnt11 = 0;
        if (n % 2 == 1) {
            for (int l = 0, r = m - 1; l < r; l++, r--) {
                if (grid[n / 2][l] != grid[n / 2][r]) {
                    cnt01++;
                } else {
                    if (grid[n / 2][l] == 0) {
                        cnt00++;
                    } else {
                        cnt11++;
                    }
                }
            }
        }
        if (m % 2 == 1) {
            for (int l = 0, r = n - 1; l < r; l++, r--) {
                if (grid[l][m / 2] != grid[r][m / 2]) {
                    cnt01++;
                } else {
                    if (grid[l][m / 2] == 0) {
                        cnt00++;
                    } else {
                        cnt11++;
                    }
                }
            }
        }
        if (n % 2 == 1 && m % 2 == 1) {
            res += grid[n / 2][m / 2];
        }
        // cout << res << ' ' << cnt00 << ' ' << cnt01 << ' ' << cnt11 << endl;
        if (cnt01 != 0) {
            return res + cnt01;
        } else {
            if (cnt11 * 2 % 4 != 0) {
                return res + 2;
            } else {
                return res;
            }
        }
    }
};

