class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = INT_MAX, cur = 0;
        for(int i = 0; i < n/2; i++)
        {
            int j = 0, k = m-1;
            while(j < k)
            {
                int c = 0;
                if(grid[i][j] == 0)
                {
                    c++;
                }
                if(grid[i][k] == 0)
                {
                    c++;
                }
                if(grid[n-i-1][j] == 0)
                {
                    c++;
                }
                if(grid[n-i-1][k] == 0)
                {
                    c++;
                }
                j++;
                k--;
                cur += min(c, 4-c);
            }   
        }
        int x = 0, y = 0;
        if(n%2 != 0)
        {
            for(int i = 0; i < m/2; i++)
            {
                if(grid[n/2][i] != grid[n/2][m-i-1])
                {
                    cur++;
                    x++;
                }
                else if(grid[n/2][i] == 1)
                {
                    y++;
                }
            }
        }
        if(m%2 != 0)
        {
            for(int i = 0; i < n/2; i++)
            {
                if(grid[i][m/2] != grid[n-i-1][m/2])
                {
                    cur++;
                    x++;
                }
                else if(grid[i][m/2] == 1)
                {
                    y++;
                }
            }
        }
        if(n%2 != 0 && m%2 != 0 && grid[n/2][m/2] == 1)
        {
            cur++;
        }
        if(y%2 != 0 && x == 0)
        {
            cur += 2;
        }
        return cur;
    }
};
