class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row=grid.size(),col=grid[0].size();
        int ans0=0;
        for(int i0=0,i1=row-1;i0<i1;++i0,--i1)
        {
            for(int j0=0,j1=col-1;j0<j1;++j0,--j1)
            {
                int c=grid[i0][j0]+grid[i0][j1]+grid[i1][j0]+grid[i1][j1];
                if(c==1 || c==3) ans0+=1;
                else if(c==2) ans0+=2;
            }
        }
        int c00=0,c01=0,c11=0;
        if(row%2==1 && col%2==1)
        {
            for(int i=row/2,j=0,k=col-1;j<k;++j,--k)
            {
                if(grid[i][j]==0 && grid[i][k]==0) c00+=1;
                else if(grid[i][j]==1 && grid[i][k]==1) c11+=1;
                else c01+=1;
            }
            for(int j=col/2,i=0,k=row-1;i<k;++i,--k)
            {
                if(grid[i][j]==0 && grid[k][j]==0) c00+=1;
                else if(grid[i][j]==1 && grid[k][j]==1) c11+=1;
                else c01+=1;
            }
            if(c11*2%4==0) ans0+=c01;
            else ans0+=c01+(c01==0 ? 2 : 0);
            ans0+=grid[row/2][col/2];
        }
        else if(row%2==1)
        {
            for(int i=row/2,j=0,k=col-1;j<k;++j,--k)
            {
                if(grid[i][j]==0 && grid[i][k]==0) c00+=1;
                else if(grid[i][j]==1 && grid[i][k]==1) c11+=1;
                else c01+=1;
            }
            if(c11*2%4==0) ans0+=c01;
            else ans0+=c01+(c01==0 ? 2 : 0);
        }
        else if(col%2==1)
        {
            for(int j=col/2,i=0,k=row-1;i<k;++i,--k)
            {
                if(grid[i][j]==0 && grid[k][j]==0) c00+=1;
                else if(grid[i][j]==1 && grid[k][j]==1) c11+=1;
                else c01+=1;
            }
            if(c11*2%4==0) ans0+=c01;
            else ans0+=c01+(c01==0 ? 2 : 0);
        }
        return ans0;
    }
};
