class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int res=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int i2=n-1-i,j2=m-1-j;
                int cnt=grid[i][j]+grid[i][j2]+grid[i2][j]+grid[i2][j2];
                res+=min(cnt,4-cnt);
            }
        }

        int rf=0,rp=0,cf=0,cp=0;
        if(n&1){
            int i=n/2;
            for(int j=0;j<m/2;j++){
                if(grid[i][j]==1||grid[i][m-1-j]==1){
                    if(grid[i][j]==1&&grid[i][m-1-j]==1){
                        rf++;
                    }
                    else{
                        rp++;
                    }
                }
            }
        }
        if(m&1){
            int j=m/2;
            for(int i=0;i<n/2;i++){
                if(grid[i][j]==1||grid[n-1-i][j]==1){
                    if(grid[i][j]==1&&grid[n-1-i][j]==1){
                        cf++;
                    }
                    else{
                        cp++;
                    }
                }
            }
        }
        if((rf+cf)&1){
            if(!rp&&!cp){
                res+=2;
            }
        }
        res+=rp+cp;
        if((n&1)&&(m&1))res+=grid[n/2][m/2];
        return res;
    }
};
