class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size();
        int m=g[0].size();
        int ans=0;
        int a=0,b=0;
        for(int i=0;i<n;++i)
            for(int j=0;j<m;++j)
            {
                //bool flag=false;
                if(n%2==1&&i==n/2&&g[i][j])  a++;
                if(m%2==1&&j==m/2&&g[i][j])  b++;
                //ans+=flag;
            }
        int dp[2],f[2],h[2];
        dp[0]=0;    dp[1]=1e9;
        if(n%2==1&&m%2==1&&g[n/2][m/2]) ans++;
        if(n&1)
        {
            for(int j=0;j<m-1-j;++j)
            {
                int cnt=g[n/2][j]+g[n/2][m-1-j];
                f[0]=cnt;   f[1]=2-cnt;
                h[0]=dp[0]; h[1]=dp[1];
                dp[0]=min(f[0]+h[0],f[1]+h[1]);
                dp[1]=min(f[0]+h[1],f[1]+h[0]);
            }
        }
        if(m&1)
        {
            for(int i=0;i<n-1-i;++i)
            {
                int cnt=g[i][m/2]+g[n-1-i][m/2];
                f[0]=cnt;   f[1]=2-cnt;
                h[0]=dp[0]; h[1]=dp[1];
                dp[0]=min(f[0]+h[0],f[1]+h[1]);
                dp[1]=min(f[0]+h[1],f[1]+h[0]);
            }
        }
        ans+=dp[0];
        //cout<<ans<<endl;
        for(int i=0;i<n-1-i;++i)
        {
            for(int j=0;j<m-1-j;++j)
            {
                int cnt=g[i][j]+g[i][m-1-j]+g[n-1-i][j]+g[n-1-i][m-1-j];
                ans+=min(cnt,4-cnt);
            }
        }
        return ans;
    }
};
